import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Auth() {
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [gender, setGender] = useState("");
  const [role, setRole] = useState("customer");

  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async () => {
    setError("");

    if (!email || !password) {
      setError("Email and password are required");
      return;
    }

    if (!isLogin && password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    try {
      setLoading(true);

      if (isLogin) {
        const res = await axios.post(
          "http://localhost:5000/api/auth/login",
          { email, password }
        );

        const userRole = res.data.role;

        if (rememberMe) {
          localStorage.setItem("role", userRole);
        } else {
          sessionStorage.setItem("role", userRole);
        }

        if (userRole === "admin") navigate("/admin/dashboard");
        if (userRole === "staff") navigate("/staff/dashboard");
        if (userRole === "customer") navigate("/customer/dashboard");
      } else {
        await axios.post("http://localhost:5000/api/auth/register", {
          name,
          email,
          password,
          phone,
          gender,
          role
        });

        setIsLogin(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Authentication failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex bg-purple-600">
      
      {/* LEFT BRAND SECTION */}
      <div
        className="hidden md:flex w-1/2 bg-cover bg-center"
        style={{
          backgroundImage:
            "url(https://images.unsplash.com/photo-1500835556837-99ac94a94552)"
        }}
      >
        <div className="bg-purple-900/60 w-full h-full flex items-center">
          <div className="px-16 text-white">
            <h1 className="text-5xl font-bold leading-tight">
              House of Allure <br /> Beauty & Spa
            </h1>
            <p className="mt-4 text-purple-100 max-w-md">
              Premium beauty & wellness experience designed for you.
            </p>
          </div>
        </div>
      </div>

      {/* RIGHT FORM */}
      <div className="w-full md:w-1/2 flex items-center justify-center px-6">
        <div className="bg-white p-10 rounded-2xl shadow-xl w-full max-w-md">

          <h2 className="text-3xl font-bold text-purple-700 text-center mb-2">
            {isLogin ? "Login" : "Create Account"}
          </h2>

          <p className="text-center text-gray-500 mb-6">
            {isLogin
              ? "Welcome back to Glow Beauty Parlour"
              : "Join Glow & start your beauty journey"}
          </p>

          {error && (
            <p className="bg-red-100 text-red-600 text-sm p-2 rounded mb-4">
              {error}
            </p>
          )}

          {!isLogin && (
            <>
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="w-full mb-3 px-4 py-3 border rounded-lg"
              >
                <option value="customer">Customer</option>
                <option value="staff">Staff</option>
              </select>

              <input
                placeholder="Full Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="w-full mb-3 px-4 py-3 border rounded-lg"
              />

              <input
                placeholder="Phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                className="w-full mb-3 px-4 py-3 border rounded-lg"
              />

              <select
                value={gender}
                onChange={(e) => setGender(e.target.value)}
                className="w-full mb-3 px-4 py-3 border rounded-lg"
              >
                <option value="">Select Gender</option>
                <option>Female</option>
                <option>Male</option>
                <option>Other</option>
              </select>
            </>
          )}

          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full mb-3 px-4 py-3 border rounded-lg"
          />

          <div className="relative mb-3">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-3 border rounded-lg"
            />
            <span
              onClick={() => setShowPassword(!showPassword)}
              className="absolute right-4 top-3 cursor-pointer text-sm text-purple-600"
            >
              {showPassword ? "Hide" : "Show"}
            </span>
          </div>

          {!isLogin && (
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              className="w-full mb-3 px-4 py-3 border rounded-lg"
            />
          )}

          <div className="flex items-center mb-4">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
              className="mr-2"
            />
            <span className="text-sm text-gray-600">Remember me</span>
          </div>

          <button
            onClick={handleSubmit}
            disabled={loading}
            className="w-full bg-purple-600 hover:bg-purple-700 text-white py-3 rounded-lg font-semibold transition"
          >
            {loading ? "Please wait..." : isLogin ? "Login" : "Register"}
          </button>

          <p
            onClick={() => setIsLogin(!isLogin)}
            className="text-center text-purple-600 mt-6 cursor-pointer font-medium"
          >
            {isLogin ? "Create an account" : "Already have an account?"}
          </p>
        </div>
      </div>
    </div>
  );
}

export default Auth;
