import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Auth() {
  const [isLogin, setIsLogin] = useState(true);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [gender, setGender] = useState("");
  const [role, setRole] = useState("customer");

  const navigate = useNavigate();

  const handleSubmit = async () => {
    try {
      if (isLogin) {
        const res = await axios.post(
          "http://localhost:5000/api/auth/login",
          { email, password }
        );

        const userRole = res.data.role;
        localStorage.setItem("role", userRole);

        if (userRole === "admin") navigate("/admin/dashboard");
        if (userRole === "staff") navigate("/staff/dashboard");
        if (userRole === "customer") navigate("/customer/dashboard");
      } else {
        await axios.post("http://localhost:5000/api/auth/register", {
          name,
          email,
          password,
          phone,
          gender,
          role
        });

        alert("Registered successfully");
        setIsLogin(true);
      }
    } catch (err) {
      alert(err.response?.data?.message || "Something went wrong");
    }
  };

  return (
  <div className="min-h-screen flex bg-[#f6f9f8]">
    
    {/* LEFT IMAGE / BRAND SECTION */}
    <div
      className="hidden md:flex w-1/2 items-center justify-center bg-cover bg-center"
      style={{
        backgroundImage:
          "url(https://images.unsplash.com/photo-1500835556837-99ac94a94552)"
      }}
    >
      <div className="bg-black/40 w-full h-full flex items-center">
        <div className="px-16 text-white">
          <h1 className="text-5xl font-bold leading-tight">
            House of Allure <br /> Beauty & Spa
          </h1>
          <p className="mt-4 text-lg text-gray-200 max-w-md">
            Relax your body and mind with our premium beauty & wellness services.
          </p>
        </div>
      </div>
    </div>

    {/* RIGHT FORM SECTION */}
    <div className="w-full md:w-1/2 flex items-center justify-center px-6">
      <div className="bg-white rounded-2xl shadow-xl p-10 w-full max-w-md">

        <h2 className="text-3xl font-bold text-purple-700 text-center mb-2">
          {isLogin ? "Login" : "Create Account"}
        </h2>

        <p className="text-center text-gray-500 mb-6">
          {isLogin
            ? "Welcome back to Glow Beauty Parlour"
            : "Join Glow & start your beauty journey"}
        </p>

        {/* REGISTER ONLY */}
        {!isLogin && (
          <>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full mb-3 px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-400"
            >
              <option value="customer">Customer</option>
              <option value="staff">Staff</option>
            </select>

            <input
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full mb-3 px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-400"
            />

            <input
              placeholder="Phone Number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="w-full mb-3 px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-400"
            />

            <select
              value={gender}
              onChange={(e) => setGender(e.target.value)}
              className="w-full mb-3 px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-400"
            >
              <option value="">Select Gender</option>
              <option>Female</option>
              <option>Male</option>
              <option>Other</option>
            </select>
          </>
        )}

        {/* COMMON FIELDS */}
        <input
          type="email"
          placeholder="Email Address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full mb-3 px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-400"
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full mb-4 px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-400"
        />

        <button
          onClick={handleSubmit}
          className="w-full bg-purple-600 hover:bg-purple-700 text-white py-3 rounded-lg font-semibold transition"
        >
          {isLogin ? "Login" : "Register"}
        </button>

        <p
          onClick={() => setIsLogin(!isLogin)}
          className="text-center text-purple-600 mt-6 cursor-pointer font-medium"
        >
          {isLogin ? "Create an account" : "Already have an account?"}
        </p>
      </div>
    </div>
  </div>
);

}

export default Auth;
