import { useState } from "react";
import axios from "../utils/axios";

export default function BookAppointment() {
  const [date, setDate] = useState("");
  const [service, setService] = useState("");

  const book = async () => {
    await axios.post("/appointments", { date, service });
    alert("Appointment Booked Successfully ðŸ’–");
  };

  return (
   <div className="min-h-screen bg-purple-100 flex justify-center items-center">

      <div className="bg-white p-8 rounded-xl shadow-md w-96">
        <h2 className="text-2xl font-bold text-purple-600 mb-6 text-center">

          Book Appointment
        </h2>

        <input
          type="date"
          className="w-full border p-2 mb-4 rounded"
          onChange={(e) => setDate(e.target.value)}
        />

        <select
          className="w-full border p-2 mb-4 rounded"
          onChange={(e) => setService(e.target.value)}
        >
          <option>Select Service</option>
          <option>Facial</option>
          <option>Haircut</option>
          <option>Makeup</option>
        </select>

        <button
          onClick={book}
          className="w-full bg-purple-600 text-white py-2 rounded hover:bg-pur[;e-700"
        >
          Confirm Booking
        </button>
      </div>
    </div>
  );
}
